import { useEffect, useCallback, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from './context/AuthContext';
import apiClient from './api/client'; // Axios 인스턴스 import
import { getProfile } from './api/auth';
import { toast } from 'react-toastify';

/**
 * 소셜 로그인 결과를 처리하는 핸들러 컴포넌트
 * 백엔드 팝업으로부터 메시지를 받아 로그인 처리를 완료하는 역할
 */
const SocialLoginHandler = () => {
  const { login } = useAuth();
  const navigate = useNavigate();

  // 디버깅 함수
  const debug = (message) => {
    console.log("[SocialLoginHandler]", message);
  };

  // useCallback으로 함수 메모이제이션
  const handleMessage = useCallback(async (event) => {
    // 1) HMR 메시지 무시
    if (event.data?.type === 'webpackOk') {
      debug("HMR 메시지 무시");
      return;
    }
    
    // 2) 출처 확인 - 개발 환경에서는 여러 Origin 허용 (백엔드에서 '*'로 postMessage 할 수 있음)
    const allowedOrigins = ['http://localhost:8000', 'http://127.0.0.1:8000', window.location.origin];
    debug(`메시지 수신: origin=${event.origin}`);

    // '*' 원본으로 보낸 메시지거나 허용된 origin인 경우에만 처리
    if (event.origin && !allowedOrigins.includes(event.origin)) {
      debug(`허용되지 않은 오리진: ${event.origin} (허용: ${allowedOrigins.join(', ')})`);
      // Origin 확인 실패해도 계속 진행 (와일드카드 메시지 처리용)
    }
    
    // 3) 메시지 타입 확인
    const { type, payload } = event.data || {};
    debug(`수신된 메시지 타입: ${type}, 데이터:`, event.data);
    
    if (type !== 'social-login-success') {
      debug(`지원되지 않는 메시지 타입: ${type}`);
      return;
    }
    
    debug("소셜 로그인 성공 메시지를 처리합니다");
    const { access_token, refresh_token, user_email } = payload || {};
    debug(`사용자 이메일: ${user_email || '알 수 없음'}`);
    
    // 토큰 유효성 검사
    if (!access_token || !refresh_token) {
      debug("토큰 정보가 누락되었습니다");
      toast.error("로그인 정보가 유효하지 않습니다");
      return;
    }

    debug(`토큰 정보 확인됨: ${access_token.substring(0, 10)}...`);
    
    try {
      // 1. 로컬 스토리지에 토큰 저장
      localStorage.setItem('access_token', access_token);
      localStorage.setItem('refresh_token', refresh_token);
      debug("토큰이 로컬 스토리지에 저장됨");
      
      // 2. API 클라이언트 인증 헤더 설정
      apiClient.defaults.headers.common['Authorization'] = `Bearer ${access_token}`;
      debug("인증 헤더 설정 완료");
      
      // 3. 사용자 프로필 정보 가져오기
      debug("사용자 프로필 요청 시작...");
      const userData = await getProfile();
      debug("사용자 프로필 수신 성공:", userData);
      
      // 4. 인증 컨텍스트 업데이트
      login(access_token, refresh_token, userData);
      debug("로그인 완료! 인증 컨텍스트 업데이트됨");
      
      // 5. 성공 메시지 표시
      toast.success(`${userData.name || userData.email || '사용자'}님 환영합니다!`, {
        position: "top-center",
        autoClose: 3000
      });
      
      // 6. 홈 페이지로 리다이렉트
      debug("홈 페이지로 이동합니다");
      navigate('/', { replace: true });
      
      // 7. 소셜 로그인 성공 이벤트 발생 (옵션)
      window.dispatchEvent(new CustomEvent('socialLoginComplete', { 
        detail: { success: true, provider: user_email ? user_email.split('@')[1] : 'unknown' } 
      }));
      
    } catch (error) {
      console.error('소셜 로그인 처리 중 오류:', error);
      debug(`오류 세부 정보: ${error.message}`);
      
      if (error.response) {
        debug(`서버 응답: ${error.response.status} - ${JSON.stringify(error.response.data)}`);
      }
      
      toast.error('로그인 처리 중 오류가 발생했습니다');
      
      // 오류 발생 시 토큰 삭제
      localStorage.removeItem('access_token');
      localStorage.removeItem('refresh_token');
      delete apiClient.defaults.headers.common['Authorization'];
      
      navigate('/login');
    }
  }, [login, navigate]);

  // 이벤트 리스너 등록 및 제거 (StrictMode 중복 방지)
  const listenerRegistered = useRef(false);
  useEffect(() => {
    if (!listenerRegistered.current) {
      debug("메시지 이벤트 리스너 등록");
      window.addEventListener('message', handleMessage);
      listenerRegistered.current = true;
    }
    return () => {
      if (listenerRegistered.current) {
        debug("메시지 이벤트 리스너 제거");
        window.removeEventListener('message', handleMessage);
        listenerRegistered.current = false;
      }
    };
  }, [handleMessage]);

  return null; // UI 렌더링 없음
};

export default SocialLoginHandler;
